mapper.delete:
	delete from 

	where 1=1 
		
		 and lbtId = #{lbtId} 
		
		
		 and lbtCode = #{lbtCode}
		
		
		 and lbtType = #{lbtType}
		
		
		 and lbtStatus = #{lbtStatus}
		
		
		 and lbtImgSrc = #{lbtImgSrc}
		
		
		 and description = #{description}
		
		
		 and orderNum = #{orderNum}
		
		
		 and effective = #{effective}
		
		
		 and createUserId = #{createUserId}
		
		
		 and createTime = #{createTime}
		
		
		 and modifyUserId = #{modifyUserId}
		
		
		 and modifyTime = #{modifyTime}

mapper.select:
	select 

	from 

	where 1=1 
		
		 and lbtId = #{lbtId} 
		
		
		 and lbtCode = #{lbtCode}
		
		
		 and lbtType = #{lbtType}
		
		
		 and lbtStatus = #{lbtStatus}
		
		
		 and lbtImgSrc = #{lbtImgSrc}
		
		
		 and description = #{description}
		
		
		 and orderNum = #{orderNum}
		
		
		 and effective = #{effective}
		
		
		 and createUserId = #{createUserId}
		
		
		 and createTime = #{createTime}
		
		
		 and modifyUserId = #{modifyUserId}
		
		
		 and modifyTime = #{modifyTime}

	select 

	from 

	where 1=1 
		
		 and lbtId = #{lbtId} 
		
		
		 and lbtCode = #{lbtCode}
		
		
		 and lbtType = #{lbtType}
		
		
		 and lbtStatus = #{lbtStatus}
		
		
		 and lbtImgSrc = #{lbtImgSrc}
		
		
		 and description = #{description}
		
		
		 and orderNum = #{orderNum}
		
		
		 and effective = #{effective}
		
		
		 and createUserId = #{createUserId}
		
		
		 and createTime = #{createTime}
		
		
		 and modifyUserId = #{modifyUserId}
		
		
		 and modifyTime = #{modifyTime}
		
	order by (orderNum+0)

	select 

	from 

	where 1=1 
		
		 and lbtId != #{lbtId} 
		
		
		 and lbtCode = #{lbtCode}
		
		
		 and lbtType = #{lbtType}
		
		
		 and lbtStatus = #{lbtStatus}
		
		
		 and lbtImgSrc = #{lbtImgSrc}
		
		
		 and description = #{description}
		
		
		 and orderNum = #{orderNum}
		
		
		 and effective = #{effective}
		
		
		 and createUserId = #{createUserId}
		
		
		 and createTime = #{createTime}
		
		
		 and modifyUserId = #{modifyUserId}
		
		
		 and modifyTime = #{modifyTime}
		
	order by (orderNum+0)
