mapper.delete:
	delete from 

	where 1=1 
		
		 and productCategoryDetailId = #{productCategoryDetailId} 
		
		
		and productCategoryId = #{productCategoryId}
		
		
		 and productId = #{productId}
		
		
		 and orderNum = #{orderNum}
		
		
		 and effective = #{effective}
		
		
		 and createUserId = #{createUserId}
		
		
		 and createTime = #{createTime}
		
		
		 and modifyUserId = #{modifyUserId}
		
		
		 and modifyTime = #{modifyTime}

mapper.select:
	select 

	from 

	where 1=1 
		
		 and productCategoryDetailId = #{productCategoryDetailId} 
		
		
		and productCategoryId = #{productCategoryId}
		
		
		 and productId = #{productId}
		
		
		 and orderNum = #{orderNum}
		
		
		 and effective = #{effective}
		
		
		 and createUserId = #{createUserId}
		
		
		 and createTime = #{createTime}
		
		
		 and modifyUserId = #{modifyUserId}
		
		
		 and modifyTime = #{modifyTime}

	select 

	from 

	where 1=1 
		
		 and productCategoryDetailId = #{productCategoryDetailId} 
		
		
		and productCategoryId = #{productCategoryId}
		
		
		 and productId = #{productId}
		
		
		 and orderNum = #{orderNum}
		
		
		 and effective = #{effective}
		
		
		 and createUserId = #{createUserId}
		
		
		 and createTime = #{createTime}
		
		
		 and modifyUserId = #{modifyUserId}
		
		
		 and modifyTime = #{modifyTime}
		
	order by (orderNum+0)

	select 

	from 

	where 1=1 
		
		 and productCategoryDetailId != #{productCategoryDetailId} 
		
		
		and productCategoryId = #{productCategoryId}
		
		
		 and productId = #{productId}
		
		
		 and orderNum = #{orderNum}
		
		
		 and effective = #{effective}
		
		
		 and createUserId = #{createUserId}
		
		
		 and createTime = #{createTime}
		
		
		 and modifyUserId = #{modifyUserId}
		
		
		 and modifyTime = #{modifyTime}
		
	order by (orderNum+0)
