mapper.update:
	update

	set 
		defaultStatus = '01',
		modifyUserId = #{modifyUserId},
		modifyTime = #{modifyTime}
	where 
		receiveAddressId = #{receiveAddressId} 
		and appUserId = #{appUserId}
		and defaultStatus = '00'
		and effective = '01'

	update

	set 
		defaultStatus = '00',
		and effective = '00',
		modifyUserId = #{modifyUserId},
		modifyTime = #{modifyTime}
	where 
		receiveAddressId = #{receiveAddressId} 
		and appUserId = #{appUserId}
		and effective = '01'

mapper.delete:
	delete from 

	where 1=1
		
		 and receiveAddressId = #{receiveAddressId} 
		
		
		 and appUserId = #{appUserId}
		
		
		 and receicerName = #{receicerName}
		
		
		 and phone = #{phone}
		
		
		 and province = #{province}
		
		
		 and city = #{city}
		
		
		 and district = #{district}
		
		
		 and street = #{street}
		
		
		 and detail = #{detail}
		
		
		 and defaultStatus = #{defaultStatus}
		
		
		 and orderNum = #{orderNum}
		
		
		 and effective = #{effective}
		
		
		 and createUserId = #{createUserId}
		
		
		 and createTime = #{createTime}
		
		
		 and modifyUserId = #{modifyUserId}
		
		
		 and modifyTime = #{modifyTime}

mapper.select:
	select 

	from 

	where 1=1
		
		 and receiveAddressId = #{receiveAddressId} 
		
		
		 and appUserId = #{appUserId}
		
		
		 and receicerName = #{receicerName}
		
		
		 and phone = #{phone}
		
		
		 and province = #{province}
		
		
		 and city = #{city}
		
		
		 and district = #{district}
		
		
		 and street = #{street}
		
		
		 and detail = #{detail}
		
		
		 and defaultStatus = #{defaultStatus}
		
		
		 and orderNum = #{orderNum}
		
		
		 and effective = #{effective}
		
		
		 and createUserId = #{createUserId}
		
		
		 and createTime = #{createTime}
		
		
		 and modifyUserId = #{modifyUserId}
		
		
		 and modifyTime = #{modifyTime}

	select 

	from 

	where 1=1
		
		 and receiveAddressId = #{receiveAddressId} 
		
		
		 and appUserId = #{appUserId}
		
		
		 and receicerName = #{receicerName}
		
		
		 and phone = #{phone}
		
		
		 and province = #{province}
		
		
		 and city = #{city}
		
		
		 and district = #{district}
		
		
		 and street = #{street}
		
		
		 and detail = #{detail}
		
		
		 and defaultStatus = #{defaultStatus}
		
		
		 and orderNum = #{orderNum}
		
		
		 and effective = #{effective}
		
		
		 and createUserId = #{createUserId}
		
		
		 and createTime = #{createTime}
		
		
		 and modifyUserId = #{modifyUserId}
		
		
		 and modifyTime = #{modifyTime}
		
	order by (orderNum+0)

	select 

	from 

	where 1=1
		
		 and receiveAddressId != #{receiveAddressId} 
		
		
		 and appUserId = #{appUserId}
		
		
		 and receicerName = #{receicerName}
		
		
		 and phone = #{phone}
		
		
		 and province = #{province}
		
		
		 and city = #{city}
		
		
		 and district = #{district}
		
		
		 and street = #{street}
		
		
		 and detail = #{detail}
		
		
		 and defaultStatus = #{defaultStatus}
		
		
		 and orderNum = #{orderNum}
		
		
		 and effective = #{effective}
		
		
		 and createUserId = #{createUserId}
		
		
		 and createTime = #{createTime}
		
		
		 and modifyUserId = #{modifyUserId}
		
		
		 and modifyTime = #{modifyTime}
		
	order by (orderNum+0)
